const express = require('express');
const { getAESkey } = require('../../utils');
var CryptoJS = require("crypto-js");
var crypto = require('crypto');
const fs = require('fs');
const router = express.Router();
const axios = require('axios');

router.get('/', async (req, res) => {
    try {

        // Generating Unique Session key for individual request
        const AES_KEY = getAESkey().toString();

        const requestJSON = {
            Name: "Amit Sharma",
            FileType: "PDF",
            SignatureType: 3,
            SelectPage: "FIRST",
            SignaturePosition: "Bottom-Right",
            AuthToken: "fb896a7b-1b4f-42b3-b9e5-22943cc5972e",
            File: "",
            PageNumber: null,
            PreviewRequired: true,
            PagelevelCoordinates: null,
            CustomizeCoordinates: null,
            SUrl: "http://localhost:52322/Success",
            FUrl: "http://localhost:52322/Error",
            CUrl: "http://localhost:52322/Cancel",
            ReferenceNumber: "REFDB100011",
            Enableuploadsignature: true,
            Enablefontsignature: true,
            EnableDrawSignature: true,
            EnableeSignaturePad: true,
            IsCompressed: false,
            IsCosign: true,
            EnableViewDocumentLink: true,
            Storetodb: true,
            IsGSTN: true,
            IsGSTN3B: false
        }


        // Encrypting request data with AES key
        var encryptedJSON = CryptoJS.AES.encrypt(JSON.stringify(requestJSON), AES_KEY);
        encryptedJSON = encryptedJSON.toString();
        console.log("Encrypted Request--->", encryptedJSON)


        // Generating Hash of encrypted data;
        var hashedRequest = CryptoJS.SHA256(encryptedJSON.toString());
        hashedRequest = hashedRequest.toString();
        console.log("Generated Hash--->", hashedRequest)


        // Encrypting Generated Hash with AES key
        var encryptedHash = CryptoJS.AES.encrypt(hashedRequest.toString(), AES_KEY);
        encryptedHash = encryptedHash.toString();
        console.log("Encrypted Hash--->", encryptedHash)


        // Getting encrypted certificate
        const publicKey = fs.readFileSync(__dirname + '/publicKey/certificate.cer', { encoding: 'utf8', flag: 'r' });;
        console.log("CERTIFICATE ---->", publicKey);


        // Encrypting Session key with Public Key of emSigner
        const encryptedSessionKey = crypto.publicEncrypt(publicKey, Buffer.from(AES_KEY));

        console.log("ENCRYPTED SESSION KEY ----->", encryptedSessionKey.toString("base64"))

        const axiosPostData = {
            Parameter1: encryptedSessionKey.toString("base64"),
            Parameter2: encryptedJSON,
            Parameter3: encryptedHash
        }

        console.log(axiosPostData);

        // Calling ESign API 
        let eSignRes = await axios.post('https://demosignergateway.emsigner.com/eMsecure/V3_0/Index',
            {
                axiosPostData
            }, {})


        res.json({
            data: eSignRes.data,
        });


    } catch (err) {
        console.error("ERROR ------>", err);
        res.json({
            message: err.message,
        }).status(500);
    }
});


module.exports = router;
